        classified_info: function(json, callback) { //游记编辑
            var data = {};
            data.TagList = [{
                TagName: '自由行',
                IsSelected: false
            }, {
                TagName: '摄影',
                IsSelected: true
            }, {
                TagName: '徒步',
                IsSelected: true
            }, {
                TagName: '购物',
                IsSelected: false
            }];
            data.PracticalInfo = {
                TravelDays: '12',
                CompanionType: '三人行',
                Consume: '5000',
                DepartureDate: '2013-02-09'
            }
            callback(data);
        },
        del_journal: function(json) { //删除游记接口
            var url = DETAIL_API.del_journal;
            $.post(url, json, function(data) {
                if (data.RetCode == 1) {
                    $.popupbox.show({
                        id: 'after_delJournal'
                    });
                    var timer = $('#after_delJournal').find('span').text();
                    var time_link = self.setInterval(function() {
                        timer = timer - 1;
                        $('#after_delJournal').find('span').text(timer);
                        if (timer == 0) {
                            clearInterval(time_link);
                            DETAIL_API.del_journal_callback();
                        };
                    }, 1000);
                } else {
                    alert(data.ErrorMessage)
                }
            })
        },
        add_comment: function(json) { //增加评论
            var url = DETAIL_API.add_comment;
            $.post(url, json, function(data) {
                if (data.RetCode == 1) {
                    if (data.Html != '') {
                        $.gsmessages.notice({
                            text: data.Html,
                            color: 'b',
                            timeout: 3000
                        });
                    };
                    DETAIL_API.add_comment_callback(json);
                    $('#picComment').fadeOut(600, function() {
                        $.popupbox.close();
                        //重置状态
                        $('#pic_content').val('');
                        $('#picComment').removeData('quote');
                    });
                } else {
                    alert(data.ErrorMessage)
                }
            })
        }
    };

